package level1;

/* 두 수 A,B를 입력받은 후 합을 출력 */

import java.util.*;

public class ex6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int A, B;
		A = sc.nextInt(); //공백 이전까지의 int를 입력받음
		B = sc.nextInt();
		System.out.println(A + B);
	}

}
